    for (let i = 0; i < inputString.length; i++) {
        let char = inputString[i];
        if (charMap[char] !== undefined) {
            result += charMap[char];
        } else {
            result += char;
        }
    }
    return result;
}

function get_sign(link_Urlpath, link_Params) {
    const {java} = this;
    var time = String(Date.now());
    let xst = time.substr(0, 10);
    let skey = "797f446d-21ee-442d-bf18-79f3b22e7f06"
    let api_Params = {
        "app_name": "com.wbydq.txtreader",
        "app_vc": "208",
        "app_vn": "2.0.8",
        "dev_id": "995a1caa-4748-3da9-8a79-7de88ffc2700",
        "language": "zh_cn",
        "ts": time,
    }


   obj = Object.assign(api_Params, link_Params);
    p = Object.keys(obj).sort().map(key=>key+"="+obj[key]).join('&');

    url = "https://new.wowonongjia.com";
    if(/new/g.test(link_Urlpath)){
    	url = "https://read.wowonongjia.com";
    	p = String(p).replace("language=zh_cn&","");  	
    	}else{
    		url = url;
      	p = p;
    	}

    	data = url + link_Urlpath + p + xst + "+"  + skey;
    md5text = java.md5Encode(data).toUpperCase();
    let sign = transformString(String(md5text));

    head = JSON.stringify({
	"headers":{
        "xss": sign,
        "xst": xst,
        "User-Agent": "okhttp/4.11.0"
    }});

    return url+link_Urlpath+"?"+p+","+head;
}

